mapper.update:
UPDATE project
name=#{proj_name}, start_date=#{start_date}, end_sched_date=#{end_sched_date}, end_date=#{end_date}, pstatus_no=#{pstatus_no}, prod_no=#{prod_no}, first_pay=#{first_pay}
WHERE proj_no = #{proj_no}

UPDATE schedule

	name=#{sched_name},
	start_date=#{start_date},
	end_date=#{end_date}

WHERE proj_no = #{proj_no} AND sched_no = #{sched_no}

mapper.delete:
DELETE FROM schedule
WHERE proj_no = #{proj_no}

	AND sched_no = #{sched_no}

mapper.update:
UPDATE proj_member
SET rtype_no = #{rtype_no}
WHERE proj_no = #{proj_no} AND
emp_no = #{emp_no}
